import React, { useState, useEffect } from "react";
import {
  Button,
  Typography,
  RadioGroup,
  FormControlLabel,
  Radio,
  Box,
  Paper,
} from "@mui/material";
import RestartAltIcon from "@mui/icons-material/RestartAlt";
import { womenQuestions } from "./WomenQuestionsData";

const LOCAL_STORAGE_KEY = "womenAnswers";

export default function WomenQuestions({ name, onSubmit, onBack }) {
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    try {
      const storedAnswers = localStorage.getItem(LOCAL_STORAGE_KEY);
      if (storedAnswers) {
        setAnswers(JSON.parse(storedAnswers));
      }
    } catch (error) {
      console.error("Failed to load answers from localStorage", error);
    }
  }, []);

  const handleAnswerChange = (id, value) => {
    const newAnswers = { ...answers, [id]: value === "yes" };
    setAnswers(newAnswers);
    try {
      localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(newAnswers));
    } catch (error) {
      console.error("Failed to save answers to localStorage", error);
    }
  };

  const handleSubmit = () => {
    onSubmit(answers);
    try {
      localStorage.removeItem(LOCAL_STORAGE_KEY);
    } catch (error) {
      console.error("Failed to remove answers from localStorage", error);
    }
    setAnswers({});
  };

  const handleReset = () => {
    try {
      localStorage.removeItem(LOCAL_STORAGE_KEY);
    } catch (error) {
      console.error("Failed to remove answers from localStorage", error);
    }
    setAnswers({});
  };

  const allAnswered = womenQuestions.every((q) => q.id in answers);

  return (
    <Box sx={{ padding: 2, maxWidth: 600, margin: "0 auto" }}>
      <Typography variant="h5" gutterBottom>
        Additional Questions for {name}
      </Typography>

      <Paper elevation={3} sx={{ p: 3, borderRadius: 2, bgcolor: "#fafafa" }}>
        {womenQuestions.map((q) => (
          <Box key={q.id} sx={{ mb: 2 }}>
            <Typography fontWeight="bold" gutterBottom>
              {q.text} *
            </Typography>
            <RadioGroup
              row
              value={
                answers[q.id] === true
                  ? "yes"
                  : answers[q.id] === false
                  ? "no"
                  : ""
              }
              onChange={(e) => handleAnswerChange(q.id, e.target.value)}
            >
              <FormControlLabel value="yes" control={<Radio />} label="Yes" />
              <FormControlLabel value="no" control={<Radio />} label="No" />
            </RadioGroup>
          </Box>
        ))}

        <Box sx={{ display: "flex", justifyContent: "space-between", mt: 3 }}>
          <Button
            variant="outlined"
            color="secondary"
            onClick={() => {
              handleReset();
              onBack();
            }}
            startIcon={<RestartAltIcon />}
          >
            Back
          </Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleSubmit}
            disabled={!allAnswered}
          >
            Submit
          </Button>
        </Box>
      </Paper>
    </Box>
  );
}
