import React, { useState, useEffect } from "react";
import {
  Button,
  Typography,
  RadioGroup,
  FormControlLabel,
  Radio,
  Box,
  Divider,
  useMediaQuery,
} from "@mui/material";
import RestartAltIcon from "@mui/icons-material/RestartAlt";
import { useTheme } from "@mui/material/styles";
import { womenQuestions } from "./WomenQuestionsData";

const LOCAL_STORAGE_KEY = "womenAnswers";

export default function WomenQuestions({ name, onSubmit, onBack }) {
  const [answers, setAnswers] = useState({});
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    try {
      const storedAnswers = localStorage.getItem(LOCAL_STORAGE_KEY);
      if (storedAnswers) {
        setAnswers(JSON.parse(storedAnswers));
      }
    } catch (error) {
      console.error("Failed to load answers from localStorage", error);
    }
  }, []);

  const handleAnswerChange = (id, value) => {
    const newAnswers = { ...answers, [id]: value === "yes" };
    setAnswers(newAnswers);
    try {
      localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(newAnswers));
    } catch (error) {
      console.error("Failed to save answers to localStorage", error);
    }
  };

  const handleSubmit = () => {
    onSubmit(answers);
    try {
      localStorage.removeItem(LOCAL_STORAGE_KEY);
    } catch (error) {
      console.error("Failed to remove answers from localStorage", error);
    }
    setAnswers({});
  };

  const handleReset = () => {
    try {
      localStorage.removeItem(LOCAL_STORAGE_KEY);
    } catch (error) {
      console.error("Failed to remove answers from localStorage", error);
    }
    setAnswers({});
  };

  const allAnswered = womenQuestions.every((q) => q.id in answers);

  return (
    <Box
      sx={{
        padding: { xs: "1rem", sm: "2rem" },
        maxWidth: "700px",
        margin: "0 auto",
        position: "relative",
        zIndex: 1,
        backgroundColor: "rgba(255, 255, 255, 0.9)",
        borderRadius: 2,
        boxShadow: 3,
      }}
    >
      <Typography variant="h4" gutterBottom align="center">
        Additional Questions for {name}
      </Typography>

      <Divider sx={{ my: 2 }} />

      {womenQuestions.map((q) => (
        <Box key={q.id} sx={{ mb: 3 }}>
          <Typography variant="body1" sx={{ mb: 1 }}>
            {q.text} *
          </Typography>

          <RadioGroup
            row={!isMobile}
            value={
              answers[q.id] === true
                ? "yes"
                : answers[q.id] === false
                ? "no"
                : ""
            }
            onChange={(e) => handleAnswerChange(q.id, e.target.value)}
          >
            <FormControlLabel value="yes" control={<Radio />} label="Yes" />
            <FormControlLabel value="no" control={<Radio />} label="No" />
          </RadioGroup>

          <Divider sx={{ my: 2 }} />
        </Box>
      ))}

      <Box
        sx={{
          display: "flex",
          justifyContent: isMobile ? "center" : "space-between",
          gap: isMobile ? 2 : 0,
          mt: 3,
        }}
      >
        <Button
          variant="outlined"
          color="secondary"
          onClick={() => {
            handleReset();
            onBack();
          }}
          startIcon={<RestartAltIcon />}
          sx={{ fontWeight: "bold" }}
        >
          Back
        </Button>

        <Button
          variant="contained"
          color="primary"
          onClick={handleSubmit}
          disabled={!allAnswered}
          sx={{
            boxShadow: "0 3px 8px rgba(255, 0, 0, 0.3)",
            fontWeight: "bold",
          }}
        >
          Submit
        </Button>
      </Box>

      <Box sx={{ mt: 4, textAlign: "center" }}>
        <Typography variant="body2" color="text.secondary">
          Â© {new Date().getFullYear()} Created by Maria Thomas.
        </Typography>
      </Box>
    </Box>
  );
}
