import React from "react";
import { BrowserRouter as Router, Routes, Route, NavLink } from "react-router-dom";
import { AppBar, Toolbar, Stack, Container, Typography, useTheme, useMediaQuery, Box } from "@mui/material";
import HomeIcon from "@mui/icons-material/Home";

import QuestionForm from "./components/QuestionForm";
import WomenPage from "./pages/WomenPage";

function App() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Router>
      <AppBar
        position="static"
        sx={{
          backgroundColor: "#f9f9f9",
          color: "#222",
          transition: "all 0.3s ease",
          "&:hover": {
            backgroundColor: "#fff",
            transform: "scale(1.02)",
            boxShadow: "0 6px 24px rgba(0,0,0,0.1)",
          },
        }}
        elevation={1}
      >
        <Container maxWidth="lg">
          <Toolbar disableGutters sx={{ justifyContent: "space-between" }}>
            <Typography
              variant={isMobile ? "h6" : "h4"} // Bigger on larger screens
              component={NavLink}
              to="/"
              sx={{
                textDecoration: "none",
                color: "inherit",
                fontWeight: 800,
                display: "flex",
                alignItems: "center",
                gap: 1.2,
                textShadow: "1px 1px 2px rgba(0,0,0,0.15)", // Light text shadow for emphasis
                transition: "all 0.3s ease",
                "&:hover": {
                  color: theme.palette.primary.main,
                  textShadow: "2px 2px 4px rgba(0,0,0,0.2)",
                },
              }}
            >
              <HomeIcon fontSize="large" />
              Blood Donation Eligibility
            </Typography>

            <Stack direction="row" spacing={isMobile ? 1 : 3}>
              {/* Add navigation links here */}
            </Stack>
          </Toolbar>
        </Container>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Routes>
          <Route path="/" element={<QuestionForm />} />
          <Route path="/women" element={<WomenPage />} />
          <Route
            path="*"
            element={
              <Box textAlign="center" mt={10}>
                <Typography variant="h2" color="error" gutterBottom>
                  404
                </Typography>
                <Typography variant="h5" color="text.secondary" gutterBottom>
                  Page Not Found
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  The page you are trying to access does not exist or has been moved.
                </Typography>
              </Box>
            }
          />
        </Routes>
      </Container>
    </Router>
  );
}

export default App;
